import React, { useState, useEffect } from "react";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import axios from "axios";

const App = () => {
  const [properties, setProperties] = useState([]);
  const [search, setSearch] = useState("");
  const [showModal, setShowModal] = useState(false);

  const handleSearch = () => {
    if (!search.trim()) return;
    axios
      .get(`http://localhost:8080/api/properties}`)
      .then((res) => setProperties(res.data))
      .catch((err) => console.error(err));
  };

  return (
    <div style={{ height: "100vh", width: "100vw", position: "relative" }}>
      {/* Top right "About Us" button */}
      <button
        onClick={() => setShowModal(true)}
        style={{
          position: "absolute",
          top: 20,
          right: 20,
          padding: "8px 12px",
          backgroundColor: "#007BFF",
          color: "white",
          border: "none",
          borderRadius: 5,
          zIndex: 1000,
        }}
      >
        About Us
      </button>

      {/* Title and Search Bar */}
      <div style={{ textAlign: "center", paddingTop: 50 }}>
        <h1 style={{ fontSize: "2em", marginBottom: "10px" }}>
          Property Assessment Tool
        </h1>
        <input
          type="text"
          placeholder="Enter District or State"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          style={{
            padding: "10px",
            width: "300px",
            fontSize: "1em",
            borderRadius: "5px",
            border: "1px solid #ccc",
          }}
        />
        <button
          onClick={handleSearch}
          style={{
            marginLeft: "10px",
            padding: "10px 15px",
            fontSize: "1em",
            borderRadius: "5px",
            border: "none",
            backgroundColor: "#28a745",
            color: "white",
          }}
        >
          Search
        </button>
      </div>

      {/* Map Area */}
      <div style={{ marginTop: 20, height: "70%" }}>
        <MapContainer
          center={[10.85, 76.27]}
          zoom={8}
          style={{ height: "100%", width: "100%" }}
        >
          <TileLayer
            attribution='&copy; <a href="https://www.google.com/maps">Google Maps</a>'
            url="http://{s}.google.com/vt/lyrs=y&x={x}&y={y}&z={z}"
            subdomains={["mt0", "mt1", "mt2", "mt3"]}
          />

          {properties.map((property, idx) => (
            <Marker
              key={idx}
              position={[property.latitude, property.longitude]}
            >
              <Popup>
                <strong>{property.houseName}</strong>
                <br />
                {property.city}, {property.pincode}
              </Popup>
            </Marker>
          ))}
        </MapContainer>
      </div>

      {/* About Us Modal */}
      {showModal && (
        <div
          style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: "rgba(0,0,0,0.5)",
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            zIndex: 2000,
          }}
        >
          <div
            style={{
              backgroundColor: "white",
              padding: 30,
              borderRadius: 8,
              width: "90%",
              maxWidth: 500,
              textAlign: "center",
            }}
          >
            <h2>About the Company</h2>
            <p>
              Property Assessment Tool is a platform designed to provide
              intelligent, location-based property insights across Kerala. We
              help urban planners, local governments, and individuals assess
              property data accurately using geo-mapping, risk indicators, and
              location intelligence.
            </p>
            <button
              onClick={() => setShowModal(false)}
              style={{
                marginTop: 20,
                padding: "8px 20px",
                backgroundColor: "#dc3545",
                color: "white",
                border: "none",
                borderRadius: 5,
              }}
            >
              Close
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default App;
